using UnityEngine;
using Gululu.Util;


namespace Gululu
{
    public class ChildAvatorUtils
    {
        public static Sprite readChildDefaultAvatorImage(string childSn)
        {
            string childDefaultPath = Application.persistentDataPath + "/" +  childSn + "640.png";
            return ImageBase64.readImageAvator(childDefaultPath, 640);
        }

        public static Sprite readChild640AvatorImage(string childSn)
        {
            string childDefaultPath = Application.persistentDataPath + "/" + childSn + "640.png";
            return ImageBase64.readImageAvator(childDefaultPath, 640);
        }

        public static Sprite readChild320AvatorImage(string childSn)
        {
            string childDefaultPath = Application.persistentDataPath + "/" + childSn + "320.png";
            return ImageBase64.readImageAvator(childDefaultPath, 320);
        }

        public static Sprite readChild160AvatorImage(string childSn)
        {
            string childDefaultPath = Application.persistentDataPath + "/" + childSn + "160.png";
            return ImageBase64.readImageAvator(childDefaultPath, 160);
        }


                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  

